import React , {Component} from 'react'
import {setUp , run_depth_first_search} from './Algorithms/depth_first_search_maze'


class App extends Component{
  constructor(){
    super()
    this.state = {
      cols: Math.floor(40 * (Math.floor(window.innerWidth * .9) / Math.floor(window.innerHeight * .9))), // min 20 x 20 max 50 x 50
      rows: 40,
      width: Math.floor(window.innerWidth * .9),
      height: Math.floor(window.innerHeight * .9)
    }
  }

  updateCanvas = () => {
    let {cols , rows , width , height} = this.state
    const canvas = this.refs.maze
    const c = canvas.getContext('2d');

    setUp({c , canvas , cols , rows, width , height , draw_maze: true })
  }

  render(){
    let {width , height} = this.state
    return(
      <div className="mt-1">
        <nav class="navbar navbar-light bg-light">
          <button onClick={() => this.updateCanvas()}>Generate Array</button>
          <button onClick={() => {
            run_depth_first_search()
            }}>solve maze</button>
        </nav>
        {/* <div className="text-center mr-1">
          <button onClick={() => this.updateCanvas()}>Generate Array</button>
          <button onClick={() => {
            run_depth_first_search()
            }}>solve maze</button>
        </div> */}
        <div className="m-5">
          <canvas ref="maze" style={{width, height}}></canvas>
        </div> 
      </div>
    )
  }
}

export default App
