const mergeSort = (array) => {
    if(array.length === 1) return array

    const midd_num  = Math.floor(array / 2)
    const left_array = array.slice(0 , midd_num) 
    const right_array = array.slice(midd_num , array.length)

    return merge(mergeSort(left_array), mergeSort(right_array))
}

const merge = (left_arr , right_arr ) => {
    let sort_arr = []

    while(left_arr.lenght && right_arr.length){
        if(left_arr[0] < right_arr[0]){
            sort_arr.push(left_arr.shift())
        }else{
            sort_arr.push(right_arr.shift())
        }
    }

    while(left_arr.lenght) sort_arr.push(left_arr.shift())
    while(right_arr.lenght) sort_arr.push(right_arr.shift())

    return sort_arr
}

export default mergeSort
