let path = []

function merge_sort(array){
  if(array.length === 1) return array 

  const midd = Math.floor(array.length / 2)
  const left_arr = array.slice(0,midd)
  const right_arr = array.slice(midd, array.length)

  return merge(merge_sort(left_arr), merge_sort(right_arr))
}

const merge = (left_array, right_array) => {
  let sort_array = []

  while(left_array.length && right_array.length){
    let {min_index, min_left_node , min_right_node} = find_min_node(left_array , right_array)
    if(left_array[0].height < right_array[0].height){
        let left_node = left_array[0]
        // let {min_index, min_left_node , min_right_node} = find_min_node(left_array)
      if(left_node.index !== min_index){
        if(min_left_node){
          min_left_node.index = left_node.index
        }else{
          min_right_node.index = left_node.index
        }
        left_node.index = min_index
      }
      path.push(left_node)
      sort_array.push(left_array.shift())
    }else{
      let right_node = right_array[0]
    //   let {min_index, min_left_node , min_right_node} = find_min_node(right_array)
      if(right_node.index !== min_index){
        if(min_left_node){
          min_left_node.index = right_node.index
        }else{
          min_right_node.index = right_node.index
        }
        right_node.index = min_index
      }
      path.push(right_node)
      sort_array.push(right_array.shift())
    }
  }

  while(left_array.length) {
    let left_n = left_array[0]
    let {min_index, min_left_node , min_right_node} = find_min_node(left_array,right_array)
    if(left_n.index !== min_index){
      if(min_left_node){
        min_left_node.index = left_n.index
      }else{
        min_right_node.index = left_n.index
      }
      left_n.index = min_index
    }
    path.push(left_n)
    sort_array.push(left_array.shift())
  }
  while(right_array.length) {
    let right_n = right_array[0]
    let {min_index, min_right_node , min_left_node } = find_min_node(left_array,right_array)
    if(right_n.index !== min_index){
      if(min_left_node){
        min_left_node.index = right_n.index
      }else{
        min_right_node.index = right_n.index
      }
      right_n.index = min_index
    }
    path.push(right_n)
    sort_array.push(right_array.shift())
  }
  return sort_array
}

const find_min_node = (left_array = [],right_array = []) => {
  let com_arr = [...left_array,...right_array]
  let min_index =  Math.min(...com_arr.map(element => element.index))

  let min_left_node = left_array.find(element => element.index === min_index)

  let min_right_node = right_array.find(element => element.index === min_index)

  return {min_index, min_left_node , min_right_node}
}

const solve_array = array => {
  let answer = merge_sort(array)
  if(answer) return path
}

export default solve_array
