import React , {Component} from 'react'

class App extends Component{
  constructor(){
    super()
    this.state = {
      array: []
    }
  }

  randomNUmber = (min,max) => {
    return Math.floor(Math.random() * (max - min) + min)
  }

  generateArray = () => {
    let array = []
    for(let i = 0; i < 100 ; i ++) {
      array.push(this.randomNUmber(5, 1000))
    }

    this.setState({array})
  }

  render(){
    return(
      <div>
        <div className="container" style={{width: '100vw' , height: '70vh'}}>
          {this.state.array.map((element , index) => 
            <div className="sort_bar" key={index} style={{width: '2px', height: `${element}px`, margin: '0 1px'}}>
            </div>
          )}
        </div> 
        <button onClick={() => this.generateArray()}>Generate Array</button>
      </div>
    )
  }
}

export default App
