import React , {Component} from 'react'
import merge_sort from './Algorithms/mergeSort'

class App extends Component{
  constructor(){
    super()
    this.state = {
      array: []
    }
  }

  randomNUmber = (min,max) => {
    return Math.floor(Math.random() * (max - min) + min)
  }

  generateArray = () => {
    let array = []
    for(let i = 0; i < 100 ; i ++) {
      array.push(this.randomNUmber(5, 500))
    }

    this.setState({array})
  }

  mergeSort = () => {
    // let array = merge_sort(this.state.array)
    // this.setState({array})
    for(let i = 0; i <  this.state.array.length ; i ++ ){
      let bars = document.getElementsByClassName('bar').children
      setTimeout(() => {
        console.log(bars)
        // i > 0 ? bars[i - 1].style.backgroundColor = 'blue' : null
      },i * 10);
    }
  }

  render(){
    return(
      <div>
        <div className="container mt-5 bar_container" style={{width: '100vw' , height: '70vh'}}>
          {this.state.array.map((element , index) => 
            <div className="bar" id = {`${index} bar`}key={index} style={{height: `${element}px`, backgroundColor: 'blue'}}>
            </div>
          )}
        </div> 
        <button onClick={() => this.generateArray()}>Generate Array</button>
        <button onClick={() => this.mergeSort()}>Merge Sort</button>
      </div>
    )
  }
}

export default App
