import { Block } from '../helper_method'

let start_node , end_node , nodes , c , canvas , size 

let open_list_1 , close_list_1 , current_node_1 , open_list_2 , close_list_2 , current_node_2 , myReq , finish_path , find_sorted_path

const bidirectional_dijkstra = props => {
    start_node = props.start_node
    start_node.distance = 0
    end_node = props.end_node 
    nodes = props.nodes
    c = props.c 
    canvas = props.canvas 
    size = props.size 

    open_list_1 = [start_node]
    close_list_1 = []

    open_list_2 = [end_node]
    close_list_2 = []

    current_node_1 = null 
    current_node_2 = null 

    finish_path = false 
    find_sorted_path = false 

    cancelAnimationFrame(myReq)
    run_solve_maze()
}

const stop_bidirectional_dijkstra = () => {
    cancelAnimationFrame(myReq)
}

const run_solve_maze = () => {
    myReq = requestAnimationFrame(run_solve_maze)
    c.clearRect(0,0,canvas.width, canvas.height)

    for(let i = 0 ; i < nodes.length ; i ++){
        nodes[i].draw()
    }

    for(let i = 0 ; i < close_list_1.length ; i ++){
        if(!end_node.prev_node){
            close_list_1[i].color = 'MidnightBlue'
        }
        close_list_1[i].draw()
    }

    for(let i = 0 ; i < open_list_1.length ; i ++){
        open_list_1[i].color = 'DeepSkyBlue'
        open_list_1[i].draw()
    }

    for(let i = 0 ; i < close_list_2.length ; i ++){
        if(!end_node.prev_node){
            close_list_2[i].color = 'MidnightBlue'
        }
        close_list_2[i].draw()
    }

    for(let i = 0 ; i < open_list_2.length ; i ++){
        open_list_2[i].color = 'DeepSkyBlue'
        open_list_2[i].draw()
    }

    if(current_node_1 && current_node_2 && current_node_1.x === current_node_2.x && current_node_1.y === current_node_2.y){
        find_sorted_path = true 
        finish_path = true 
    }

    if(open_list_1.length > 0 && !find_sorted_path){
        current_node_1 = open_list_1.sort((a,b) => a.distance - b.distance)[0] 
        close_list_1.push(current_node_1)
        find_child_node(current_node_1 , open_list_1 , close_list_1)
    }

    if(open_list_2.length > 0 && !find_sorted_path){
        current_node_2 = open_list_2.sort((a,b) => a.distance - b.distance)[0] 
        close_list_2.push(current_node_2)
        find_child_node(current_node_2 , open_list_2 , close_list_2)
