    if(finish_path){
        cancelAnimationFrame(myReq)
    }
}

const find_child_node = (c_node, open_list_array , close_list_array) => {

    let {top , right , bottom , left} = get_top_right_bottom_left(c_node , nodes )

    // Right (x + size , y)
    if(right && !right.walls[3] && !close_list_array.find(node => node.x === right.x  && node.y === right.y)){
        let right_in_open = open_list_array.find(n => n.x === right.x  && n.y === right.y)

        right_in_open ? update_node(right_in_open , c_node) : open_list_array.push(create_new_node(right , c_node))
    }

    // top (x , y - size)
    if(top && !top.walls[2] && !close_list_array.find(node => node.x === top.x && node.y === top.y)){
        let top_in_open = open_list_array.find(n => n.x === top.x  && n.y === top.y)

        top_in_open ? update_node(top_in_open , c_node) : open_list_array.push(create_new_node(top , c_node))
    }

    // left (x - size , y )
    if(left && !left.walls[1] && !close_list_array.find(node => node.x === left.x && node.y === left.y)){
        let left_in_open = open_list_array.find(n => n.x === left.x  && n.y === left.y)

        left_in_open ? update_node(left_in_open , c_node) : open_list_array.push(create_new_node(left , c_node))
    }

    // bottom (x , y + size)
    if(bottom && !bottom.walls[0] &&!close_list_array.find(node => node.x === bottom.x && node.y === bottom.y)){
        let bottom_in_open = open_list_array.find(n => n.x === bottom.x  && n.y === bottom.y)

        bottom_in_open ? update_node(bottom_in_open , c_node) : open_list_array.push(create_new_node(bottom , c_node))
    }

    open_list_array = open_list_array.filter(node => node.x === c_node.x && node.y === c_node.y ? false : true )
}

