import React , {Component} from 'react'
import {setUp , run_depth_first_search} from '../Algorithms/depth_first_search_maze'

class Navbar extends Component {
    updateCanvas = () => {
        let cols , rows , width , height 
        cols = Math.floor(40 * (Math.floor(window.innerWidth * .9) / Math.floor(window.innerHeight * .9))) // min 20 x 20 max 50 x 50
        rows = 40
        width = Math.floor(window.innerWidth * .9)
        height = Math.floor(window.innerHeight * .9)

        let {canvas} = this.props
        let c = canvas.getContext('2d');

        setUp({c , canvas , cols , rows, width , height , draw_maze: true })
    }
    render(){
        return(
            <nav class="navbar">
                <button 
                    onClick={() => this.updateCanvas()}
                >
                    Generate Array
                </button>
                <button onClick={() => {
                    run_depth_first_search()
                    }}
                >
                    Solve Maze
                </button>
            </nav>
        )
    }
}

export default Navbar
