import React , {Component} from 'react'
import {setUp , draw_first_search_maze} from './Algorithms/depth_first_search_maze'


class App extends Component{
  constructor(){
    super()
    this.state = {
      size: 40, 
      cols: 35, 
      rows: 20
    }
  }

  componentDidMount(){
    let {cols , rows , size } = this.state
    const canvas = this.refs.maze
    const c = canvas.getContext('2d');

    setUp({c , canvas , cols , rows , size})
    draw_first_search_maze()
  }

  updateCanvas = () => {
    draw_first_search_maze()
  }

  render(){
    let {cols , rows , size } = this.state
    return(
      <div className="container">
        <canvas ref="maze" style={{width: size * cols , height: size * rows}}></canvas>
        <button onClick={() => this.updateCanvas()}>Generate Array</button>
      </div>
    )
  }
}

export default App
