import React , {Component} from 'react'

class App extends Component{
  constructor(){
    super()
    this.state = {
      array: []
    }
  }

  generateArray = () => {
    let array = []
    for(let i = 0; i < 100 ; i ++) {
      array.push(randomNUmber(5, 1000))
    }

    this.setState({array})
  }

  render(){
    return(
      <div className="bar_array">
        {this.state.array.map((element , index) => {
          <div className="sort_bar" key={index}>
            {element}
          </div>
        })}
        <button onClick={() => this.generateArray()}>Generate Array</button>
      </div>
    )
  }
}

const randomNUmber = (min,max) => {
  return Math.floor(Math.random() * (max - min) + min)
}

export default App
